import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:get/get_rx/src/rx_types/rx_types.dart';import '../../help/Colors.dart';import '../../help/text_style.dart';class Home_Button_big extends StatelessWidget {  final void Function() onPressed;  const Home_Button_big({    Key? key, required this.Text_Button, required this.onPressed,  }) : super(key: key);  final String Text_Button;  @override  Widget build(BuildContext context) {    final RxInt X = 55.obs;    return Container(      margin: EdgeInsets.only(bottom: 20),      height: MediaQuery.of(context).size.longestSide / 30,      width: MediaQuery.of(context).size.longestSide / 4.5,      child: Obx(       () {          return ElevatedButton(            onHover: (value) {              X.value = 65;              Future.delayed(const Duration(milliseconds: 500), () {                X.value = 55;              });            },            child: Padding(              padding: const EdgeInsets.only(top: 10),              child: Text(Text_Button),            ),            onPressed: () {             onPressed();            },            style: ElevatedButton.styleFrom(                primary: Colorsapp.SecondColor,                textStyle: Text_Style.getstyle(                    fontsize: MediaQuery.of(context).size.longestSide / X.value,                    ColorText: Colorsapp.mainColor,                    fontWeight: FontWeight.w700)),          );        }      ),    );  }}